client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import NotFound from "@/pages/not-found";
import { BubbleAnimation } from "@/components/ui/bubble-animation";
import { FigmaPageTransition } from "@/components/ui/figma-gradient-ball";
import { TransitionProvider, usePageTransition } from "@/contexts/TransitionContext";

import { HomePage } from "@/pages/HomePage";
import { JobNavBar } from "@/pages/JobNavBar";
import { JobDetailsPage } from "@/pages/JobDetailsPage";
import { ApplyJobPage } from "@/pages/ApplyJobPage";
import { UserProfilePage } from "@/pages/UserProfilePage";
import { CompanyProfilePage } from "@/pages/CompanyProfilePage";
import { DashboardPage } from "@/pages/DashboardPage";
import { LoginPage } from "@/pages/LoginPage";
import { SettingsPage } from "@/pages/SettingsPage";
import { WorkshopPage } from "@/pages/WorkshopPage";
import { ResumePage } from "@/pages/ResumePage";
import { PreparationPage } from "@/pages/PreparationPage";
import { CulturalEventsPage } from "@/pages/CulturalEventsPage";

function Router() {
  return (
    <Switch>
      {/* Add pages below */}
      <Route path="/" component={HomePage} />
      <Route path="/jobs" component={JobNavBar} />
      <Route path="/course/:id" component={WorkshopPage} />
      <Route path="/job/:id" component={JobDetailsPage} />
      <Route path="/apply/:id" component={ApplyJobPage} />
      <Route path="/profile" component={UserProfilePage} />
      <Route path="/company/:id" component={CompanyProfilePage} />
      <Route path="/dashboard" component={DashboardPage} />
      <Route path="/login" component={LoginPage} />
      <Route path="/settings" component={SettingsPage} />
      {/* More dropdown pages */}
      <Route path="/workshop" component={WorkshopPage} />
      <Route path="/resume" component={ResumePage} />
      <Route path="/preparation" component={PreparationPage} />
      <Route path="/cultural-events" component={CulturalEventsPage} />
      {/* Fallback to 404 */}
      <Route component={NotFound} />
    </Switch>
  );
}

function AppContent() {
  const { isTransitioning } = usePageTransition();

  return (
    <div className="relative min-h-screen bg-gradient-to-br from-neutral-50 to-neutral-100">
      {/* Background pattern */}
      <div className="absolute inset-0 bg-grid-pattern opacity-5" />

      {/* Main content with Figma gradient ball transitions */}
      <FigmaPageTransition 
        isTransitioning={isTransitioning}
        ballSize="xl"
        bounceType="medium"
        className="relative z-10"
      >
        <Toaster />
        <Router />
      </FigmaPageTransition>

      {/* Optional bubble animation overlay */}
      <BubbleAnimation isVisible={isTransitioning} />
    </div>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TransitionProvider>
        <AppContent />
      </TransitionProvider>
    </QueryClientProvider>
  );
}

export default App;

client/src/index.css.content:
@tailwind components;
@tailwind utilities;

@layer components {
  .all-\[unset\] {
    all: unset;
  }
}

:root {
  --app-secondary: rgba(0, 191, 166, 1);
  --body-font-family: "Sora", Helvetica;
  --body-font-size: 18px;
  --body-font-style: normal;
  --body-font-weight: 400;
  --body-letter-spacing: 0px;
  --body-line-height: normal;
  --caption-font-family: "Sora", Helvetica;
  --caption-font-size: 12px;
  --caption-font-style: normal;
  --caption-font-weight: 400;
  --caption-letter-spacing: 0px;
  --caption-line-height: normal;
  --h5-font-family: "Sora", Helvetica;
  --h5-font-size: 28px;
  --h5-font-style: normal;
  --h5-font-weight: 400;
  --h5-letter-spacing: 0px;
  --h5-line-height: normal;
  --primay: rgba(103, 58, 183, 1);
}

:root {
  --animate-spin: spin 1s linear infinite;
}

.animate-fade-in {
  animation: fade-in 1s var(--animation-delay, 0s) ease forwards;
}

.animate-fade-up {
  animation: fade-up 1s var(--animation-delay, 0s) ease forwards;
}

.animate-marquee {
  animation: marquee var(--duration) infinite linear;
}

.animate-marquee-vertical {
  animation: marquee-vertical var(--duration) linear infinite;
}

.animate-shimmer {
  animation: shimmer 8s infinite;
}

.animate-spin {
  animation: var(--animate-spin);
}

@keyframes spin {
  to {
    transform: rotate(1turn);
  }
}

@keyframes image-glow {
  0% {
    opacity: 0;
    animation-timing-function: cubic-bezier(0.74, 0.25, 0.76, 1);
  }

  10% {
    opacity: 0.7;
    animation-timing-function: cubic-bezier(0.12, 0.01, 0.08, 0.99);
  }

  to {
    opacity: 0.4;
  }
}

@keyframes fade-in {
  0% {
    opacity: 0;
    transform: translateY(-10px);
  }

  to {
    opacity: 1;
    transform: none;
  }
}

@keyframes fade-up {
  0% {
    opacity: 0;
    transform: translateY(20px);
  }

  to {
    opacity: 1;
    transform: none;
  }
}

@keyframes shimmer {
  0%,
  90%,
  to {
    background-position: calc(-100% - var(--shimmer-width)) 0;
  }

  30%,
  60% {
    background-position: calc(100% + var(--shimmer-width)) 0;
  }
}

@keyframes marquee {
  0% {
    transform: translate(0);
  }

  to {
    transform: translateX(calc(-100% - var(--gap)));
  }
}

@keyframes marquee-vertical {
  0% {
    transform: translateY(0);
  }

  to {
    transform: translateY(calc(-100% - var(--gap)));
  }
}

/* Bubble Animation Keyframes */
.animate-bubble-float {
  animation: bubble-float var(--duration, 2.5s) ease-out forwards;
}

@keyframes bubble-float {
  0% {
    transform: translateY(100vh) scale(0);
    opacity: 0;
  }

  10% {
    opacity: 0.8;
    transform: translateY(90vh) scale(0.1);
  }

  50% {
    opacity: 1;
    transform: translateY(50vh) scale(1) rotate(180deg);
  }

  100% {
    opacity: 0;
    transform: translateY(-10vh) scale(0.5) rotate(360deg);
  }
}

/* Page transition bubble overlay */
.animate-bubble-burst {
  animation: bubble-burst 0.8s ease-out forwards;
}

@keyframes bubble-burst {
  0% {
    transform: scale(0);
    opacity: 0.8;
  }

  50% {
    transform: scale(1.2);
    opacity: 1;
  }

  100% {
    transform: scale(2);
    opacity: 0;
  }
}

/* Bounce Animation Keyframes */
.animate-bounce-custom {
  animation: bounce-custom 1.2s ease-out;
}

.animate-elastic {
  animation: elastic 1s ease-out;
}

.animate-spring {
  animation: spring 1.5s ease-out;
}

.animate-bounce-click {
  animation: bounce-click 0.3s ease-out;
}

@keyframes bounce-custom {
  0%, 100% {
    transform: translateY(0) scale(1);
    animation-timing-function: cubic-bezier(0.8, 0, 1, 1);
  }
  25% {
    transform: translateY(-50px) scale(1.1);
    animation-timing-function: cubic-bezier(0, 0, 0.2, 1);
  }
  50% {
    transform: translateY(-100px) scale(1.2);
    animation-timing-function: cubic-bezier(0.8, 0, 1, 1);
  }
  75% {
    transform: translateY(-30px) scale(1.05);
    animation-timing-function: cubic-bezier(0, 0, 0.2, 1);
  }
}

@keyframes elastic {
  0% {
    transform: scale(1);
  }
  25% {
    transform: scale(1.25) rotate(5deg);
  }
  50% {
    transform: scale(0.9) rotate(-3deg);
  }
  75% {
    transform: scale(1.1) rotate(2deg);
  }
  100% {
    transform: scale(1) rotate(0deg);
  }
}

@keyframes spring {
  0% {
    transform: scale(1) translateY(0);
  }
  20% {
    transform: scale(1.1) translateY(-20px);
  }
  40% {
    transform: scale(0.95) translateY(-10px);
  }
  60% {
    transform: scale(1.05) translateY(-5px);
  }
  80% {
    transform: scale(0.98) translateY(-2px);
  }
  100% {
    transform: scale(1) translateY(0);
  }
}

@keyframes bounce-click {
  0%, 100% {
    transform: scale(1);
  }
  50% {
    transform: scale(0.95);
  }
}

/* Smooth page transitions */
.page-transition-enter {
  opacity: 0;
  transform: translateY(20px);
}

.page-transition-enter-active {
  opacity: 1;
  transform: translateY(0);
  transition: opacity 0.5s ease-out, transform 0.5s ease-out;
}

.page-transition-exit {
  opacity: 1;
  transform: translateY(0);
}

.page-transition-exit-active {
