import React, { useState, useRef, useEffect } from "react";
import { NavigationHeader } from "@/components/ui/navigation-header";
import { JobListSection } from "./sections/JobListSection";
import { User, GraduationCap, Briefcase, Globe, MoreHorizontal, LogOut } from "lucide-react";
import { usePageTransition } from "@/contexts/TransitionContext";
import { BounceButton } from "@/components/ui/bounce-animation";

export const JobNavBar = (): JSX.Element => {
  const { location, navigateWithBubbles } = usePageTransition();
  const [isMoreDropdownOpen, setIsMoreDropdownOpen] = useState(false);
  const dropdownRef = useRef<HTMLDivElement>(null);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setIsMoreDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const moreMenuItems = [
    { label: "Workshop", route: "/workshop" },
    { label: "Resume", route: "/resume" },
    { label: "Preparation", route: "/preparation" },
    { label: "Cultural Events", route: "/cultural-events" },
  ];

  const navigationItems = [
    {
      icon: User,
      route: "/profile",
      label: "Profile",
      testId: "nav-profile"
    },
    {
      icon: GraduationCap,
      route: "/",
      label: "Home",
      testId: "nav-home"
    },
    {
      icon: Briefcase,
      route: "/jobs",
      label: "Jobs",
      testId: "nav-jobs"
    },
    {
      icon: Globe,
      route: "/network",
      label: "Network",
      testId: "nav-network"
    },
    {
      icon: MoreHorizontal,
      route: "/more",
      label: "More",
      testId: "nav-more"
    },
    {
      icon: LogOut,
      route: "/login",
      label: "Logout",
      testId: "nav-logout"
    },
  ];

  const handleNavigation = (route: string) => {
    navigateWithBubbles(route);
  };

  const handleMoreClick = () => {
    setIsMoreDropdownOpen(!isMoreDropdownOpen);
  };

  const handleMoreMenuClick = (route: string) => {
    setIsMoreDropdownOpen(false);
    navigateWithBubbles(route);
  };

  return (
    <div className="bg-neutral-100 w-screen min-h-screen flex flex-col">
      <NavigationHeader title="Jobs" showBackButton={false} />

      <div className="flex flex-1 min-h-0">
        <div className="relative">
          <nav className="w-[102px] bg-gradient-to-b from-[#673ab7] to-[#5e35b1] rounded-r-[64px] flex flex-col items-center justify-center py-8 relative z-10 shadow-2xl sticky top-0 h-screen">
            {/* Navigation items - simple icon layout */}
            <div className="flex flex-col items-center justify-center gap-8 flex-1">
              {navigationItems.map((item, index) => {
                const Icon = item.icon;
                const isActive = location === item.route;
                const isMoreButton = item.label === "More";

                return (
                  <BounceButton
                    key={index}
                    data-testid={item.testId}
                    onClick={isMoreButton ? handleMoreClick : () => handleNavigation(item.route)}
                    className={`
                      flex items-center justify-center w-12 h-12 
                      transition-all duration-300 ease-out
                      ${isActive 
                        ? 'text-white transform scale-110' 
                        : 'text-white/90 hover:text-white hover:scale-105'
                      }
                      ${isMoreButton && isMoreDropdownOpen ? 'text-white transform scale-110' : ''}
                    `}
                    title={item.label}
                    variant="ghost"
                    size="sm"
                    bounceOnClick={true}
                  >
                    <Icon size={24} strokeWidth={2} />
                  </BounceButton>
                );
              })}
            </div>
          </nav>

          {/* More Dropdown Menu */}
          {isMoreDropdownOpen && (
            <div 
              ref={dropdownRef}
              className="absolute left-[102px] top-0 bg-gradient-to-b from-[#673ab7] to-[#5e35b1] rounded-lg shadow-2xl z-20 min-w-[200px] py-4 px-6"
              style={{ 
                top: `${navigationItems.findIndex(item => item.label === "More") * 56 + 200}px` 
              }}
            >
              {/* More Title */}
              <div className="flex items-center gap-3 text-white font-semibold text-lg mb-4 pb-2 border-b border-white/20">
                <MoreHorizontal size={20} />
                More
              </div>

              {/* More Menu Items */}
              <div className="space-y-2">
                {moreMenuItems.map((menuItem, index) => (
                  <button
                    key={index}
                    onClick={() => handleMoreMenuClick(menuItem.route)}
                    className="w-full text-left text-white/90 hover:text-white hover:bg-white/10 rounded-md px-3 py-2 transition-all duration-200 text-base"
                  >
                    {menuItem.label}
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Main Content Area - properly aligned */}
        <main className="flex-1 bg-white shadow-inner">
          <JobListSection />
        </main>
      </div>
    </div>
  );
};

